// check whether the lib has been included - authentication!
if (!defined("lib_included")) { die("Please use index.php!"); }

$ref="mail.php?mode=view&PHPSESSID=$PHPSESSID&ID=$row[0]&action=form&form=$row[11]&page=$page&perpage=$perpage&up=$up";
tr_tag($ref);

// show icon for folders
if ($row[11] == "d") {
  echo "<td colspan=4>\n";
  // show level of record with blanks
  for ($e = 0; $e < $level; $e++) echo "&nbsp;&nbsp;&nbsp;&nbsp;";

  // show button 'open'
  // look whether elements below this level exist
  $exist = 0;
  $untouched = 0;
  $result3 = db_query("select ID, touched from mail_client where parent = '$ID'");
  while ($row3 = db_fetch_row($result3)) {
    if ($row3[0]) { $exist = 1; } // mail in this dir exist
    if (!$row3[1]) { $untouched = 1;} // at least one unred mail
  }
  if ($untouched) { echo "<b>"; }
  if (!$arrdir[$ID] and $exist and $open <> "auf") echo "<a href='mail.php?mode=view&action=open&ID=$row[0]&PHPSESSID=$PHPSESSID&filter=$filter&keyword=$keyword&sort=$sort&up=$up2&page=$page&perpage=$perpage'><img src='$img_path/close.gif' border=0>&nbsp;</a>";
  // show button 'close'
  else { echo "<a href='mail.php?mode=view&action=close&ID=$row[0]&PHPSESSID=$PHPSESSID&filter=$filter&keyword=$keyword&sort=$sort&up=$up2&page=$page&perpage=$perpage'><img src='$img_path/open.gif' border=0>&nbsp;</a>";}
  // link to form
  echo "<a href='mail.php?mode=view&PHPSESSID=$PHPSESSID&ID=$row[0]&action=form&form=$row[11]&page=$page&perpage=$perpage'>$row[2]</a>&nbsp;\n";
  if ($untouched) { echo "</b>\n"; }
  echo "</td><td colspan=2>\n";
  // delete
  //if ($row[11] == "d") {echo "<a href='mail_view.php?action=delete&delete_ID=$row[0]&PHPSESSID=$PHPSESSID' onClick=\"return confirm('$confirm')\"><img src='$img_path/r.gif' alt='$delete_it' title='$delete_it' border=0 width=7></a>&nbsp;\n";}
  if ($row[11] <> "d") {echo "<a href='mail.php?mode=view&action=delete&delete_ID=$row[0]&PHPSESSID=$PHPSESSID'><img src='$img_path/r.gif' alt='$delete_it' title='$delete_it' border=0 width=7 $check></a>&nbsp;\n";}
  echo "</td>\n";
}

// mails
else {

  // subject, first some attributes: unred, sent and level
  echo "<td>";
  // look whether this mail is unred, is yes: mark it bold
  if (!$row[10] and $row[11] <> "d") { echo "<b>"; }
  // mark it as italic if it's a sent mail
  elseif ($row[11] == "s") { echo "<i>"; }
  // show level of record with blanks
  for ($e = 0; $e < $level; $e++) echo "&nbsp;&nbsp;&nbsp;&nbsp;";

  // title
  echo "<a href='$ref'>$row[2]</a>&nbsp;</td>\n";
  // Sender
  if (ereg("&lt;",$row[4])) {$sender=explode("&lt;",$row[4]);$sender1=substr($sender[1],0,-4);$sender2=$sender[0];}
  else { $sender2 = $sender1 = $row[4]; }
  echo "<td><a href='mailto:$sender1'>$sender2</a></td>\n";

  // checkbox delete, buttons reply and forward
  // if the flag 'select_all' is set, mark the checkbox as checked
  if ($sel_all) { $flag = "checked"; }
  echo "<td><input type=checkbox name='delete_mail[]' value='$row[0]' $flag>\n";
  echo "<a href='mail.php?mode=send&action2=reply&ID=$row[0]&PHPSESSID=$PHPSESSID'><img src='$img_path/arrowleft.gif' alt='$mail_text25' title='$mail_text25' border=0 width=10></a>&nbsp;\n";
  echo "<a href='mail.php?mode=send&action2=forward&ID=$row[0]&PHPSESSID=$PHPSESSID'><img src='$img_path/arrowright.gif' alt='$mail_text26' title='$mail_text26' border=0 width=10></a>&nbsp;</td>\n";

  // date
  $datum = substr($row[13],6,2).".".substr($row[13],4,2).".".substr($row[13],0,4)." ".substr($row[13],8,2).":".substr($row[13],10,2);
  echo "<td>$datum&nbsp;</td>\n";

  // delete
  echo "<td><a href='mail.php?mode=view&action=delete&delete_ID=$row[0]&PHPSESSID=$PHPSESSID&page=$page&perpage=$perpage&up=$up'><img src='$img_path/r.gif' alt='$delete_it' title='$delete_it' border=0 width=7></a>&nbsp;</td>\n";

  // look for attachments
  echo "<td>";
  $result3 = db_query("SELECT * from mail_attach where parent = '$ID'") or db_die();
  while ($row3 = db_fetch_row($result3)) {
    // initialize random string for ID
    $rnd = "";
    srand ((double)microtime()*1000000);
    $char = "0123456789ABCDEFGHIJKLMANOPQRSTUVWXYZ";
    while (strlen($rnd) < 9) { $rnd .= substr($char,(rand()%(strlen($char))),1);}
    // determine filesize
    if ($row3[4] > 1000000)  {$fsize = floor($row3[4]/1000000)." M";}
    elseif ($row3[4] > 1000) {$fsize = floor($row3[4]/1000)." k";}
    else {$fsize = $row3[4];}
    // write data to the array for downloading
    $file_ID[$rnd] = "$row3[2]|$row3[3]|$row3[0]";
    echo "<a href='mail_down.php?rnd=$rnd&PHPSESSID=$PHPSESSID' target=_blank>$row3[2] ($fsize)</a><br>\n";
  }
  echo "</td>\n";
}

// category & remark
echo "<td>$row[7]&nbsp;</td><td>$row[8]&nbsp;</td>\n";

echo "</tr>\n";

